targets.expr:
sum by (table_id) (
  rate(stream_backfill_snapshot_read_row_count{namespace=~"$namespace",risingwave_name=~"$instance",table_id=~"$table|"}[$__rate_interval])
)
* on(table_id) group_left(table_name) (
  group(table_info{namespace=~"$namespace",risingwave_name=~"$instance"}) by (table_name, table_id)
)

